import React,{Component} from 'react'
import {Grid, Container} from 'semantic-ui-react'


export default class Customize extends Component {
render(){
return(
<Grid>
    <Grid.Row columns={2}>
        <Grid.Column>
            <Container>

                <p> this is first columsn </p>
                <canvas id="pizza" width="600" height="700"></canvas>

                {
                 window.onload = function() {
                    var c = document.getElementById("pizza");
                    var ctx = c.getContext("2d");
                    var img = document.createElement("img");
                    img.src= "https://upload.wikimedia.org/wikipedia/commons/a/a3/Eq_it-na_pizza-margherita_sep2005_sml.jpg"
                    ctx.drawImage(img, 10, 10);
                  }




                    /* <img src="https://upload.wikimedia.org/wikipedia/commons/a/a3/Eq_it-na_pizza-margherita_sep2005_sml.jpg" width="690" height="900"/> */}
                </Container>
            </Grid.Column>

            <Grid.Column>
            <Container>

                <p> this is second  columsn </p>
                </Container>
            </Grid.Column>






    </Grid.Row>




</Grid>








)
}
}
